{
    __weak typeof(self) weakSelf = self;
    NSInteger requestId;
    switch (strategy) {
        case DKCacheStrategy_CACHE_ONLY:
        // 调用获取缓存方法
            break;
        case DKCacheStrategy_NETWORK_ONLY:
        // 调用获取网络方法
            break;
        case DKCacheStrategy_NETWORK_AND_CACHE:
        // 调用网络方法,回调后写入本地缓存
            break;
